import React, { Component } from "react";

class InputsValue extends Component {
  constructor(props) {
    super(props);

    this.state = {
      inputValue: 0,
      baseSalary: 0,
      hraValue: 0,
      specialValue: 0,
      totalEarned: 0,
    };
  }
  getCTC = (event) => {
    let inputNewvalue = event.target.value;

    this.setState({
      inputValue: inputNewvalue,
    });
  };

  basicSalary = () => {
    let inputValue = this.state.inputValue;
    let baseSalary = inputValue / 2;
    let baseSalaryValue = baseSalary / 2;
    let specialValue1 = inputValue - baseSalary + baseSalaryValue;
    let totalEarnedvalue = baseSalary + baseSalaryValue + specialValue1;

    this.setState({
      inputValue: baseSalary,
      hraValue: baseSalaryValue,
      specialValue: specialValue1,
      totalEarned: totalEarnedvalue,
    });
  };

  render() {
    return (
      <div>
        <div>
          <input type="number" placeholder="enter CTC" onChange={this.getCTC} />
          <input type="number" placeholder="enter Annual Rent" />
          {/* <input type="number" placeholder="enter 80C value" />
          <input type="number" placeholder="enter 80D value" />
          <input type="number" placeholder="enter Stand. deduction" />
          <input type="text" placeholder="Metro City ? YES/NO" /> */}
          <div>
            <p>Basic Salary : {this.state.inputValue}</p>
            <p>HRA : {this.state.hraValue}</p>
            <p>Special Allowance : {this.state.specialValue}</p>
            <p>Total Earned: {this.state.totalEarned}</p>
          </div>
        </div>
        <div>
          <button onClick={this.basicSalary}>Calculate Tax</button>
        </div>
      </div>
    );
  }
}

export default InputsValue;
