import React, { Component } from "react";
import "../styleSheet.css";
import InputsValue from "./InputValue";
import Yearly from "./yearly";

class Front extends Component {
  constructor(props) {
    super(props);

    this.state = {
      ctcValue: 0,
    };
  }
  getInputFromFront = (e) =>
    this.setState({
      [e.target.name]: e.target.value,
    });

  render() {
    return (
      <div>
        <input
          type="number"
          placeholder="this in Front"
          name="ctcValue"
          onChange={this.getInputFromFront}
        />
        <Yearly data={this.state} />
      </div>
    );
  }
}

export default Front;
