import React from "react";
import "../styleSheet.css";

const SalaryComponent = (data) => {
  let ctcValue = data.ctcValue || 0;
  let metroorNot = data.metroorNot;
  let PF = data.PF;
  let ESI = data.ESI;

  let BasicSalary_value;
  BasicSalary_value = ctcValue / 2;

  let hraValue;
  if (metroorNot === "YES") {
    hraValue = (BasicSalary_value * 50) / 100;
  } else if (metroorNot === "NO") {
    hraValue = (BasicSalary_value * 40) / 100;
  } else {
    hraValue = (BasicSalary_value * 40) / 100;
  }
  let da_value = (BasicSalary_value * 10) / 100;
  let special_value = ctcValue - (BasicSalary_value + hraValue + da_value);
  let totalSalaryValue =
    BasicSalary_value + hraValue + da_value + special_value;

  let EsiSalaryvalue;
  if (ESI === "YES") {
    EsiSalaryvalue = 252000;
  } else if (ESI === "NO") {
    EsiSalaryvalue = 0;
  }
  let ESIValue;
  if (totalSalaryValue < EsiSalaryvalue) {
    ESIValue = (totalSalaryValue * 1.75) / 100;
  }

  return {
    ESIsalary: EsiSalaryvalue,
    BasicSalary: BasicSalary_value,
    Hra: hraValue,
    da: da_value,
    Special: special_value,
    totalSalary: totalSalaryValue,
    ESIdeduction: ESIValue,
  };
};

const SalaryHead = ({ data }) => {
  const salaires = SalaryComponent(data);
  return (
    <div className="mainSalary">
      <div>
        <table>
          <tr>
            <th>Earning</th>
            <th>Amount</th>
          </tr>
          <tr>
            <td>Basic Salary :</td>
            <td>{salaires.BasicSalary}</td>
          </tr>
          <tr>
            <td>D . A :</td>
            <td>{salaires.da}</td>
          </tr>
          <tr>
            <td>HRA :</td>
            <td>{salaires.Hra}</td>
          </tr>
          <tr>
            <td>Special Allowance :</td>
            <td>{salaires.Special}</td>
          </tr>
          <tr>
            <td>Total Earned :</td>
            <td>{salaires.totalSalary}</td>
          </tr>
        </table>
      </div>
      <div>
        <table>
          <tr>
            <th>Deductions</th>
            <th>Amount</th>
          </tr>
          <tr>
            <td>PT :</td>
            <td></td>
          </tr>
          <tr>
            <td>PF :</td>
            <td></td>
          </tr>
          <tr>
            <td>ESI</td>
            <td>{salaires.ESIdeduction}</td>
          </tr>
          <tr>
            <td>TDS:</td>
            <td></td>
          </tr>
          <tr>
            <td>Total Deductions :</td>
            <td></td>
          </tr>
        </table>
      </div>
    </div>
  );
};

export default SalaryHead;
