import React from "react";
import "../styleSheet.css";

const newTaxCalculation = (data) => {
  let FirstSlab = 250000;
  let SecondSlab = 500000;
  let Surcharge1 = 5000000;
  let Surcharge2 = 10000000;
  let ctcValue = data.ctcValue;

  // Tax calculation
  let new_Appl_amt1_value;
  if (ctcValue < FirstSlab) {
    new_Appl_amt1_value = ctcValue;
  } else if (ctcValue > FirstSlab) {
    new_Appl_amt1_value = FirstSlab;
  } else {
    new_Appl_amt1_value = 0;
  }
  let new_bal_amt1_value;
  if (ctcValue > FirstSlab) {
    new_bal_amt1_value = ctcValue - FirstSlab;
  } else {
    new_bal_amt1_value = 0;
  }
  let new_Appl_amt2_value;
