  }

  return {
    new_Appl_amt1: new_Appl_amt1_value,
    new_bal_amt1: new_bal_amt1_value,
  };
};

const NewTax = ({ data }) => {
  const calculation = newTaxCalculation(data);
  return (
    <div className="main_salary_componentDiv">
      <h3>Tax payabe as per new slab : 650000</h3>
      <table>
        <tr>
          <th>Tax slabs</th>
          <th>Tax Rate</th>
          <th>Applicable Amount</th>
          <th>Balance Amount</th>
          <th>Tax Amount</th>
        </tr>
        <tr>
          <td>0-2,50,000</td>
          <td>0%</td>
          <td>{calculation.new_Appl_amt1}</td>
          <td>{calculation.new_bal_amt1}</td>
          <td></td>
        </tr>
        <tr>
          <td>2,50,001-5,00,000</td>
          <td>5%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>5,00,001-7,50,000</td>
          <td>10%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>7,50,000 - 10,00,000</td>
          <td>15%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>10,00,001 - 12,50,000</td>
          <td>20%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>12,50,001 - 15,00,000</td>
          <td>25%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Above 15,00,001</td>
          <td>30%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Sub Total</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Surcharge</td>
          <td></td>
          <td></td>
          <td></td>
          <td> </td>
        </tr>
        <tr>
          <td>Health and Edu Cess</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Tax Credit (Sec 87A)</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Total Tax payable</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
      </table>
    </div>
  );
};
export default NewTax;
