import React, { Component } from "react";

class InputsValue extends Component {
  constructor(props) {
    super(props);

    this.state = {
      number: 0,
    };
  }

  /* writin a func to descrease number */

  descreaseNumber = (event) => {
    event.preventDefault();
    let number = this.state.number;
    let newNumber = number - 1;

    this.setState({
      number: newNumber,
    });
  };

  increaseNumber = () => {
    let number = this.state.number;
    let newNumber = number + 1;

    this.setState({
      number: newNumber,
    });
  };

  render() {
    return (
      <div>
        <button onClick={this.descreaseNumber}>-</button>

        <input type="text" value={this.state.number} />

        <button onClick={this.increaseNumber}>+</button>
      </div>
    );
  }
}
