import React from "react";
import "../styleSheet.css";

const newTaxCalculation = (data) => {
  let FirstSlab = 250000;
  let SecondSlab = 500000;
  let Surcharge1 = 5000000;
  let Surcharge2 = 10000000;
  let ctcValue = data.ctcValue;

  // Tax calculation
  let Appl_amt1_value;
  if (ctcValue < FirstSlab) {
    Appl_amt1_value = ctcValue;
  } else if (ctcValue > FirstSlab) {
    Appl_amt1_value = FirstSlab;
  } else {
    Appl_amt1_value = 0;
  }
  let bal_amt1_value;
  if (ctcValue > FirstSlab) {
    bal_amt1_value = ctcValue - FirstSlab;
  } else {
    bal_amt1_value = 0;
  }
  let Appl_amt2_value;
  if (bal_amt1_value >= FirstSlab) {
    Appl_amt2_value = FirstSlab;
  } else {
    Appl_amt2_value = bal_amt1_value;
  }
  let bal_amt2_value;
  if (bal_amt1_value > FirstSlab) {
    bal_amt2_value = bal_amt1_value - Appl_amt2_value;
  } else {
    bal_amt2_value = 0;
  }
  let Appl_amt3_value;
  if (bal_amt2_value > FirstSlab) {
    Appl_amt3_value = FirstSlab;
  } else {
    Appl_amt3_value = bal_amt2_value;
  }
  let bal_amt3_value;
  if (bal_amt2_value > FirstSlab) {
    bal_amt3_value = bal_amt2_value - Appl_amt3_value;
  } else {
    bal_amt3_value = 0;
  }
  let Appl_amt4_value;

  return {
    Appl_amt1: Appl_amt1_value,
    bal_amt1: bal_amt1_value,
    Appl_amt2: Appl_amt2_value,
    bal_amt2: bal_amt2_value,
    Appl_amt3: Appl_amt3_value,
    bal_amt3: bal_amt3_value,
  };
};

const NewTax = ({ data }) => {
  const calculation = newTaxCalculation(data);
  return (
    <div className="main_salary_componentDiv">
      <h3>Tax payabe as per new slab : 650000</h3>
      <table>
        <tr>
          <th>Tax slabs</th>
          <th>Tax Rate</th>
          <th>Applicable Amount</th>
          <th>Balance Amount</th>
          <th>Tax Amount</th>
        </tr>
        <tr>
          <td>0-2,50,000</td>
          <td>0%</td>
          <td>{calculation.Appl_amt1}</td>
          <td>{calculation.bal_amt1}</td>
          <td></td>
        </tr>
        <tr>
          <td>2,50,001-5,00,000</td>
          <td>5%</td>
          <td>{calculation.Appl_amt2}</td>
          <td>{calculation.bal_amt2}</td>
          <td></td>
        </tr>
        <tr>
          <td>5,00,001-7,50,000</td>
          <td>10%</td>
          <td>{calculation.Appl_amt3}</td>
          <td>{calculation.bal_amt3}</td>
          <td></td>
        </tr>
        <tr>
          <td>7,50,000 - 10,00,000</td>
          <td>15%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>10,00,001 - 12,50,000</td>
          <td>20%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>12,50,001 - 15,00,000</td>
          <td>25%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Above 15,00,001</td>
          <td>30%</td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Sub Total</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Surcharge</td>
          <td></td>
          <td></td>
          <td></td>
          <td> </td>
        </tr>
        <tr>
          <td>Health and Edu Cess</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Tax Credit (Sec 87A)</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
        <tr>
          <td>Total Tax payable</td>
          <td></td>
          <td></td>
          <td></td>
          <td></td>
        </tr>
      </table>
    </div>
  );
};
export default NewTax;
