import React, { Component } from "react";
import Yearly from "./yearly";
import "../styleSheet.css";

class InputsValue extends Component {
  constructor(props) {
    super(props);

    this.state = {
      ctcValue: 0,
      baseSalary: 0,
      hraValue: 0,
      daValue: 0,
      totalEarned: 0,
      TDS: 0,
      PT: 0,
      PF: 0,
      NetPay: 0,
    };
  }
  getCTC = (event) => {
    let ctcNewvalue = event.target.value;

    this.setState({
      ctcValue: ctcNewvalue,
    });
  };

  salaryEarned = () => {
    let ctcValue = this.state.ctcValue;
    let baseSalary = ctcValue / 2;
    let basicSalary_forHRA = baseSalary / 2;
    let daValue1 = ctcValue - (baseSalary + basicSalary_forHRA);
    let totalEarnedvalue = baseSalary + basicSalary_forHRA + daValue1;
    let TDS_value = 100;
    let PT_value = 100;
    let PF_value = 100;
    let netpay_value = totalEarnedvalue - (TDS_value + PT_value + PF_value);

    this.setState({
      baseSalary: baseSalary,
      hraValue: basicSalary_forHRA,
      daValue: daValue1,
      totalEarned: totalEarnedvalue,
      TDS: TDS_value,
      PT: PT_value,
      PF: PF_value,
      NetPay: netpay_value,
    });
  };

  render() {
    return (
      <div>
        <div>
          <input type="number" placeholder="Enter CTC" onChange={this.getCTC} />
          <input type="number" placeholder="enter Annual Rent" />
          <input type="number" placeholder="enter 80C value" />
          <input type="number" placeholder="enter 80D value" />
          <input type="number" placeholder="enter Stand. deduction" />
          <input type="text" placeholder="Metro City ? YES/NO" />
        </div>
        <div className="main_salary_componentDiv" className="display_none">
          <div className="earned_yearly">
            <h3>Monthly Earned Component</h3>
            <div>Basic Salary : {this.state.baseSalary}</div>
            <div>HRA : {this.state.hraValue}</div>
            <div>D. A : {this.state.daValue}</div>
            <div>Total Earned: {this.state.totalEarned}</div>
          </div>
          <div className="Deductions">
            <h3>Monthly deduction </h3>
            <div>TDS : {this.state.TDS}</div>
            <div>PT : {this.state.PT} </div>
            <div>PF : {this.state.PF} </div>
            <div>ESI : </div>
            <div>Net Pay:{this.state.NetPay} </div>
          </div>
        </div>
        <div>
          <button onClick={this.salaryEarned}>Calculate Tax</button>
        </div>
      </div>
    );
  }
}

export default InputsValue;
