import React from "react";
import "../styleSheet.css";
import InputsValue from "./InputValue";
import OldTax from "./oldTax";
import NewTax from "./newTax";
import SalaryHead from "./Salaries";
import Compare from "./comparision";

class Home extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  handleSubmit = (data) => {
    this.setState(data);
  };

  showNewTaxCalculation = (newTax) => {
    if (newTax !== this.state.newTax) {
      this.setState({
        newTax,
      });
    }
  };
  showOldTaxValue = (oldTax, ctcValue) => {
    // console.log(oldTax);
    if (oldTax !== this.state.oldTax) {
      this.setState({
        oldTax,
      });
    }
  };
  taxBalance = (oldTax, newTax) => {
    if (oldTax > newTax) {
      this.setState({
        taxdiff: oldTax - newTax,
      });
    } else {
      this.setState({
        taxdiff: newTax - oldTax,
      });
    }
    console.log(oldTax);
  };

  // handleTax = (event) => {
  //   console.log(event.target.newTax);
  // };

  // componentWillUnmount() {
  //   const { newTax, oldTax } = this.state;
  //   if (newTax && oldTax) {
  //     this.setState({
  //       taxDifference: newTax - oldTax >= 0 ? newTax - oldTax : oldTax - newTax,
  //     });
  //   }
  // }

  render() {
    return (
      <div>
        <div className="input_div">
          <InputsValue onSubmit={this.handleSubmit} />
        </div>
        <div className="tax_message">
          <div>Tax payable as per new Tax :{this.state.newTax}</div>
          <div>Tax payable as per Old Tax :{this.state.oldTax}</div>
          <div>Tax taxDifference :{this.taxBalance}</div>
        </div>

        <div className="mainsalary_componentDiv">
          <OldTax oldTaxDone={this.showOldTaxValue} data={this.state} />
          {/* <Compare data={this.state} /> */}
          <NewTax
            onCalculationDone={this.showNewTaxCalculation}
            data={this.state}
          />

          <SalaryHead onTDSChange={this.handleTax} data={this.state} />
        </div>
      </div>
    );
  }
}

export default Home;
