import React, { Component } from "react";
import Yearly from "./yearly";
import "../styleSheet.css";

class InputsValue extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ctcValue: 0,
      rentValue: 0,
      deduction80C: 0,
      deduction80D: 0,
      stanDard: 0,
      // taxableSalary: 0,
      // Appl_amount1: 0,
      // bal_amount1: 0,
      // Appl_amount2: 0,
      // bal_amount2: 0,
      // Appl_amount3: 0,
      // bal_amount3: 0,
      // Appl_amount4: 0,
      // sub_total_Appl: 0,
      // first_Tax: 0,
      // second_Tax: 0,
      // third_Tax: 0,
      // sub_total_tax: 0,
      // Surcharge: 0,
      // ceSS: 0,
      // taxCredit: 0,
      // totalTax: 0,
      metroorNot: "",
      // basicSalary: 0,
      // HRASalary: 0,
      // D_A_Salary: 0,
      // SpecialAllowance: 0,
    };
  }
  getInputs = (e) =>
    this.setState({
      [e.target.name]: e.target.value,
    });

  oldTaxCalcuation = () => {
    let FirstSlab = 250000;
    let SecondSlab = 500000;
    let Surcharge1 = 5000000;
    let Surcharge2 = 10000000;
    let ctcValue = this.state.ctcValue;
    let rentValue = this.state.rentValue;
    let deduction80C = this.state.deduction80C;
    let deduction80D = this.state.deduction80D;
    let stanDard = this.state.stanDard;
    let metroorNot = this.state.metroorNot;
    let basicSalaryValue = (ctcValue * 50) / 100;
    let hraValue;
    if (metroorNot === "YES") {
      hraValue = (basicSalaryValue * 50) / 100;
    }

    let taxableSalaryValue = ctcValue - deduction80C - deduction80D - stanDard;
    // Tax calculation sections
    let Appl_amount1_value;
    if (taxableSalaryValue < FirstSlab) {
      Appl_amount1_value = taxableSalaryValue;
    } else {
      Appl_amount1_value = FirstSlab;
    }
    let bal_amount1_value;
    if (taxableSalaryValue >= FirstSlab) {
      bal_amount1_value = taxableSalaryValue - FirstSlab;
    } else {
      bal_amount1_value = 0;
    }
    let Appl_amount2_value;
    if (bal_amount1_value >= FirstSlab) {
      Appl_amount2_value = FirstSlab;
    } else {
      Appl_amount2_value = bal_amount1_value;
    }
    let bal_amount2_value;
    if (Appl_amount2_value < bal_amount1_value) {
      bal_amount2_value = bal_amount1_value - Appl_amount2_value;
    } else {
      bal_amount2_value = 0;
    }
    let Appl_amount3_value;
    if (bal_amount2_value >= SecondSlab) {
      Appl_amount3_value = SecondSlab;
    } else {
      Appl_amount3_value = bal_amount2_value;
    }
    let bal_amount3_value;
    if (Appl_amount3_value < bal_amount2_value) {
      bal_amount3_value = bal_amount2_value - Appl_amount3_value;
    } else {
      bal_amount3_value = 0;
    }
    let Appl_amount4_value = bal_amount3_value;
    let sub_total_Appl_value =
      Appl_amount1_value +
      Appl_amount2_value +
      Appl_amount3_value +
      Appl_amount4_value;
    let first_TaxValue = (Appl_amount2_value * 5) / 100;
    let second_taxValue = (Appl_amount3_value * 20) / 100;
    let third_TaxValue = (Appl_amount4_value * 30) / 100;
    let sub_total_taxvalue = first_TaxValue + second_taxValue + third_TaxValue;
    let Surcharge_value;
    if (taxableSalaryValue > Surcharge1 && taxableSalaryValue < Surcharge2) {
      Surcharge_value = (sub_total_taxvalue * 10) / 100;
    } else if (taxableSalaryValue > Surcharge2) {
      Surcharge_value = (sub_total_taxvalue * 15) / 100;
    } else {
      Surcharge_value = 0;
    }
    let cess_Value;
    if (taxableSalaryValue > SecondSlab) {
      cess_Value = ((sub_total_taxvalue + Surcharge_value) * 4) / 100;
    } else {
      cess_Value = 0;
    }

    let taxCredit_Value;
    if (taxableSalaryValue <= SecondSlab) {
      taxCredit_Value = sub_total_taxvalue;
    } else {
      taxCredit_Value = 0;
    }
    let totalTaxValue =
      sub_total_taxvalue + Surcharge_value + cess_Value - taxCredit_Value;

    this.setState({
      taxableSalary: taxableSalaryValue,
      Appl_amount1: Appl_amount1_value,
      bal_amount1: bal_amount1_value,
      Appl_amount2: Appl_amount2_value,
      bal_amount2: bal_amount2_value,
      Appl_amount3: Appl_amount3_value,
      bal_amount3: bal_amount3_value,
      Appl_amount4: Appl_amount4_value,
      sub_total_Appl: sub_total_Appl_value,
      first_Tax: first_TaxValue,
      second_Tax: second_taxValue,
      third_Tax: third_TaxValue,
      sub_total_tax: sub_total_taxvalue,
      Surcharge: Surcharge_value,
      ceSS: cess_Value,
      taxCredit: taxCredit_Value,
      totalTax: totalTaxValue,
      basicSalary: basicSalaryValue,
    });
  };

  render() {
    return (
      <div>
        <div>
          <input
            type="number"
            placeholder="Enter CTC"
            name="ctcValue"
            onChange={this.getInputs}
          />
          <input
            type="number"
            placeholder="enter Annual Rent"
            name="rentValue"
            onChange={this.getInputs}
          />
          <input
            type="number"
            placeholder="enter 80C value"
            name="deduction80C"
            onChange={this.getInputs}
          />
          <input
            type="number"
            placeholder="enter 80D value"
            name="deduction80D"
            onChange={this.getInputs}
          />
          <input
            type="number"
            placeholder="enter Stand. deduction"
            name="stanDard"
            onChange={this.getInputs}
          />
          <input
            type="text"
            placeholder="Metro City ? YES/NO"
            name="metroorNot"
            onChange={this.getInputs}
          />
        </div>
        <div>
          <p>basicSalary: {this.state.basicSalary}</p>
          <button onClick={this.oldTaxCalcuation}>Calculate Tax</button>
        </div>
        <div className="main_salary_componentDiv">
          <div className="earned_yearly">
            <div>Taxable Salary is : {this.state.taxableSalary}</div>
            <h3>Tax payabe as per Old slab : 1150000</h3>
            <table>
              <tr>
                <th>Tax slabs</th>
                <th>Tax Rate</th>
                <th>Applicable Amount</th>
                <th>Balance Amount</th>
                <th>Tax Amount</th>
              </tr>
              <tr>
                <td>0-2,50,000</td>
                <td>0%</td>
                <td>{this.state.Appl_amount1}</td>
                <td>{this.state.bal_amount1}</td>
                <td>0</td>
              </tr>
              <tr>
                <td>2,50,001-5,00,000</td>
                <td>5%</td>
                <td>{this.state.Appl_amount2}</td>
                <td>{this.state.bal_amount2}</td>
                <td>{this.state.first_Tax}</td>
              </tr>
              <tr>
                <td>5,00,001-10,000,00</td>
                <td>20%</td>
                <td>{this.state.Appl_amount3}</td>
                <td>{this.state.bal_amount3}</td>
                <td>{this.state.second_Tax}</td>
              </tr>
              <tr>
                <td>Above 10,00,000</td>
                <td>30%</td>
                <td>{this.state.Appl_amount4}</td>
                <td></td>
                <td>{this.state.third_Tax}</td>
              </tr>
              <tr>
                <td>Sub Total</td>
                <td></td>
                <td>{this.state.sub_total_Appl}</td>
                <td></td>
                <td>{this.state.sub_total_tax}</td>
              </tr>
              <tr>
                <td>Surcharge</td>
                <td></td>
                <td></td>
                <td></td>
                <td>{this.state.Surcharge}</td>
              </tr>
              <tr>
                <td>Health and Edu Cess</td>
                <td></td>
                <td></td>
                <td></td>
                <td>{this.state.ceSS}</td>
              </tr>
              <tr>
                <td>Tax credit (Sec 87A)</td>
                <td></td>
                <td></td>
                <td></td>
                <td>{this.state.taxCredit}</td>
              </tr>
              <tr>
                <td>Total Tax payable</td>
                <td></td>
                <td></td>
                <td></td>
                <td>{this.state.totalTax}</td>
              </tr>
            </table>
          </div>
          <div className="earned_yearly">
            <h3>Tax payabe as per new slab </h3>
            <table>
              <tr>
                <th>Tax slabs</th>
                <th>Tax Rate</th>
                <th>Applicable Amount</th>
                <th>Balance Amount</th>
                <th>Tax Amount</th>
              </tr>
              <tr>
                <td>0-2,50,000</td>
                <td>0%</td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr>
                <td>2,50,001-5,00,000</td>
                <td>5%</td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr>
                <td>5,00,001-7,50,000</td>
                <td>10%</td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr>
                <td>7,50,000 - 10,00,000</td>
                <td>15%</td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr>
                <td>10,00,001 - 12,50,000</td>
                <td>20%</td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr>
                <td>12,50,001 - 15,00,000</td>
                <td>25%</td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr>
                <td>Above 15,00,001</td>
                <td>30%</td>
                <td></td>
                <td></td>
                <td></td>
              </tr>
              <tr>
                <td>Sub Total</td>
                <td></td>
                <td></td>
                <td></td>
                <td></td>
